import { Component } from '../core/component';
import { GameObject } from '../core/game_object';
import { RenderSettings } from './renderer_handler/core/render_settings';
import { Vector2 } from '../../../utility/math_extended/vector2';
import { Matrix3x3 } from '../../../utility/math_extended/matrix';
import { Application } from '../../../../app';

export default class Camera extends Component {
  public viewPort = new Vector2();
  public zoom = 1;
  private static _mainCamera: Camera;
  protected _type = 'Camera';
  private renderSettings: RenderSettings;
  private _worldToScreenSpaceMatrix!: Matrix3x3;
  private _screenToWorldSpaceMatrix!: Matrix3x3;
  private _viewMatrix!: Matrix3x3;

  constructor(gameObject: GameObject) {
    super(gameObject);
    if (!Camera._mainCamera) {
      Camera._mainCamera = this;
    }
    this.renderSettings = Application.instance.renderer.settings;
  }

  Awake() {
    let width = this.renderSettings.width as number;
    let height = this.renderSettings.height as number;
    let scale = this.renderSettings.UnitToPixelRatio;
    let transform = this.gameObject.transform;
    this._worldToScreenSpaceMatrix = Matrix3x3.view(width, height, scale);
    this._screenToWorldSpaceMatrix = new Matrix3x3(this._worldToScreenSpaceMatrix.inverse);

    this._screenToWorldSpaceMatrix = new Matrix3x3(this._worldToScreenSpaceMatrix.inverse);
    this._viewMatrix = Matrix3x3.viewCamera(transform.position, transform.rotation, this.zoom);
  }

  public getProperties(): RenderSettings {
    return this.renderSettings;
  }

  static get mainCamera(): Camera {
    return Camera._mainCamera;
  }

  public WorldToScreenSpace(position: Vector2): Vector2 {
    let changedPosition = new Vector2();
    let matrix = this._worldToScreenSpaceMatrix;
    changedPosition = matrix.multiplyByVector(position);
    return changedPosition;
  }

  public ScreenToWorldSpace(position: Vector2): Vector2 {
    let changedPosition = new Vector2();
    let matrix = this._screenToWorldSpaceMatrix;
    changedPosition = matrix.multiplyByVector(position);
    return changedPosition;
  }

  public ViewToScreenSpace(position: Vector2): Vector2 {
    let changedPosition = new Vector2();
    let transform = this.gameObject.transform;
    let matrix = this._worldToScreenSpaceMatrix;
    this._viewMatrix = Matrix3x3.viewCamera(transform.position, transform.rotation, this.zoom);
    changedPosition = matrix.multiplyBy(this._viewMatrix).multiplyByVector(position);

    return changedPosition;
  }

  public ViewToWorldSpace(position: Vector2): Vector2 {
    let changedPosition = new Vector2();
    let matrix = this._viewMatrix;
    changedPosition = matrix.multiplyByVector(position);
    return changedPosition;
  }

  public GetCameraScreenPosition(): Vector2 {
    // Temporarily get a new World to Screenspace Matrix
    let width = this.renderSettings.width as number;
    let height = this.renderSettings.height as number;
    let scale = this.renderSettings.UnitToPixelRatio;

    let matrix = Matrix3x3.view(width, height, scale);
    return matrix.multiplyByVector(new Vector2(this.gameObject.transform.position.x, this.gameObject.transform.position.y));
  }

  // returns the center of the screen in world space (even if camera is re-positioned)
  public GetScreenCenterToWorldSpace(): Vector2{
    let width = this.renderSettings.width as number;
    let height = this.renderSettings.height as number;
    let cameraPosition = this.GetCameraScreenPosition();
    return this.ScreenToWorldSpace(new Vector2(width - cameraPosition.x, height - cameraPosition.y));
  }

  // returns the current X coordinate of the left window side (in world space)
  public GetLeftWindowBorder(): number{
    let width = this.renderSettings.width as number;
    let cameraOffset: number = (width / 2 - this.GetCameraScreenPosition().x);
    return this.ScreenToWorldSpace(new Vector2(cameraOffset, 0)).x;
  }

  // returns the current Y coordinate of the top window side (in world space)
  public GetTopWindowBorder(): number{
    let height = this.renderSettings.height as number;
    let cameraOffset: number = (height / 2 - this.GetCameraScreenPosition().y);
    return this.ScreenToWorldSpace(new Vector2(0, cameraOffset)).y;
  }

  // returns the current X coordinate of the right window side (in world space)
  public GetRightWindowBorder(): number{
    let width = this.renderSettings.width as number;
    let cameraOffset: number = (width / 2 - this.GetCameraScreenPosition().x);
    return this.ScreenToWorldSpace(new Vector2(width + cameraOffset, 0)).x;
  }

  // returns the current Y coordinate of the bottom window side (in world space)
  public GetBottomWindowBorder(): number{
    let height = this.renderSettings.height as number;
    let cameraOffset: number = (height / 2 - this.GetCameraScreenPosition().y);
    return this.ScreenToWorldSpace(new Vector2(0, height + cameraOffset)).y;
  }
}
