import { IStartable, IUpdatable } from "./ts/core/components/assetloader/json/Interfaces/object_interfaces";
import { RenderHandler } from "./ts/core/components/rendering/renderer_handler/core/render_handeler";
import { SignalDispatcher, ISignal } from "strongly-typed-events";
import { PhysicsApp } from "./ts/box2d/box2d/physics_handler/physics_app";
import { Scene } from "./ts/core/data/models/scene";
import { Game } from "./ts/branded_games/game";
import Input from "./ts/core/components/input/input";
import { InitializeFlow } from "./ts/branded_games/initialize_flow";
import { ScreenSize } from "./ts/core/components/rendering/renderer_handler/core/render_settings";
import { PixiWorld } from "./ts/pixi/pixi_world";
import { Box2DWorld } from "./ts/box2d/box2d/physics_handler/box2d/box2d_world";
import { Vector2 } from "./ts/utility/math_extended/vector2";
import { GameObject } from "./ts/core/components/core/game_object";

export class Application implements IStartable, IUpdatable {
  //constants
  public readonly SECONDS_PER_FIXED_UPDATE: number = 0.02; //todo add value to settings JSON file
  private readonly _fpsDecimals: number = 2;
  private readonly _decimalPlacesRatio: number = Math.pow(10, this._fpsDecimals);
  private static _instance: Application;

  //public variables
  public renderer: RenderHandler;

  //private variables
  //events
  private _onAppInit: SignalDispatcher = new SignalDispatcher();
  private _onAppStart: SignalDispatcher = new SignalDispatcher();
  private _onAppUpdate: SignalDispatcher = new SignalDispatcher();
  private _onAppFixedUpdate: SignalDispatcher = new SignalDispatcher();
  private _onAppLateUpdate: SignalDispatcher = new SignalDispatcher();
  private _onAppClose: SignalDispatcher = new SignalDispatcher();
  private _onTrashCollect: SignalDispatcher = new SignalDispatcher();

  private _physicsTimeSimulated: number = 0;
  private _deltaTime: number = 0;
  private _lastUpdate: number = performance.now();

  private _fps: number = 0;
  private _timeMeasurements: number[] = [];

  public _physics: PhysicsApp;
  private _currentScene: Scene;
  //private _assetLibrary: AssetLibrary;
  private _game: Game; //todo temporary

  //getters and setters
  //events
  public get OnAppInit(): ISignal {
    return this._onAppInit.asEvent();
  }

  public get OnAppStart(): ISignal {
    return this._onAppStart.asEvent();
  }
  public get OnAppUpdate(): ISignal {
    return this._onAppUpdate.asEvent();
  }

  public get OnAppFixedUpdate(): ISignal {
    return this._onAppFixedUpdate.asEvent();
  }

  public get OnAppLateUpdate(): ISignal {
    return this._onAppFixedUpdate.asEvent();
  }

  public get OnAppClose(): ISignal {
    return this._onAppClose.asEvent();
  }
  
  public get OnTrashCollect(): ISignal {
    return this._onTrashCollect.asEvent();
  }

  //public get physics() { return this._physics }
  public get deltaTime() {
    return this._deltaTime;
  }
  public get fps(){
    return this._fps;
  }
  public get Scene() {
    return this._currentScene;
  }

  public static get instance(): Application {
    if (Application._instance == undefined) Application.CreateInstance();
    return Application._instance;
  }

  private constructor() {
    Application._instance = this;
    Input.initialize(this._onAppUpdate, this._onAppLateUpdate);

    //todo temporary
    this._game = new InitializeFlow();
    
    this._currentScene = new Scene();
    let screenSize = ScreenSize.FullScreen;
    let pixiWorld = new PixiWorld({ screenSize, UnitToPixelRatio: 50 });
    this.renderer = new RenderHandler(pixiWorld);

    this._onAppInit.sub(this.renderer.Awake.bind(this.renderer));
    //this._onAppUpdate.sub(Input.update.bind(Input));
    //this._onAppUpdate.sub(Input.mouse.update.bind(Input.mouse));
    //this._onAppUpdate.sub(Input.keyboard.update.bind(Input.keyboard));
    this._onAppUpdate.sub(this.renderer.Update.bind(this.renderer));
    this._physics = new PhysicsApp(new Box2DWorld(new Vector2(0, 0)));
  }

  public AddGameObject(object: GameObject): void {
    this._currentScene.AddGameObject(object);
  }

  public Start(): void {
    this._onAppInit.dispatch();

    this._onAppStart.dispatch();
    this._game.Start();

    this._physicsTimeSimulated = performance.now();
    this.Update();
  }

  public Update(): void {
    while (this._physicsTimeSimulated < performance.now()) {
      this._onAppFixedUpdate.dispatch();
      this._physicsTimeSimulated += this.SECONDS_PER_FIXED_UPDATE * 1000;
    }

    this._onAppUpdate.dispatch();
    this._game.Update();
    this._onAppLateUpdate.dispatch();

    // calculate Frames Per Second
    this._timeMeasurements.push(performance.now());
    const msPassed:number = this._timeMeasurements[this._timeMeasurements.length - 1] - this._timeMeasurements[0];

    if(msPassed >= 1000){
      this._fps = Math.round(this._timeMeasurements.length / msPassed * 1000 * this._decimalPlacesRatio) / this._decimalPlacesRatio;
      this._timeMeasurements = [];
    }

    this._deltaTime = performance.now() - this._lastUpdate;
    this._lastUpdate = performance.now();

    this._onTrashCollect.dispatch();

    window.requestAnimationFrame(() => {
      this.Update();
    });
  }

  public Close(): void {
    this._onAppClose.dispatch();
  }

  private static CreateInstance(): void {
    if (Application._instance == undefined) Application._instance = new Application();
  }
}
