import Input from "./input";
import { PointSet } from "../geometry/point_sets/point_set";
import { PolygonPointSet } from "../geometry/point_sets/polygon_point_set";
import _ from "lodash";
import { GameObject } from "../core/game_object";
import { Mesh } from "../geometry/shapes/mesh";
import { Vector2 } from "../../../utility/math_extended/vector2";


class GestureDetection{
    private _shape: GameObject = new GameObject("shape");

    private _userDrawnPoints: Vector2[];
    private _circleCenterPoint: Vector2;
    private _circleAvarageRadius: number;
    private _circleMarginPercentage: number = 30;
    private _isCircleDrawn = false;
    private _circleSetRadius: number;
    private _touchIndex: number = 0;

    private _originalShape: PointSet = new PointSet();
    private _originalShapeSubdivided: PointSet = new PointSet();
    private _ScaledUpShape: PointSet = new PointSet();
    private _ScaledDownShape: PointSet = new PointSet();
    private _subdivisionNumber: number = 0;
    private _shapeMarginPercentage: number = 50;
    private _isShapeDrawn = false;

    public get isCircleDrawn():boolean{
        return this._isCircleDrawn;
    }
    public get isShapeDrawn(): boolean{
        return this._isShapeDrawn;
    }

    public get touchIndex():number{
        return this._touchIndex;
    }

    public get shape():PointSet{

        return _.cloneDeep(this._originalShape);
    }

    public get shapeScaledUp():PointSet{

        return _.cloneDeep(this._ScaledUpShape);
    }

    public get shapeScaledDown():PointSet{

        return _.cloneDeep(this._ScaledDownShape);
    }

    public get subdivisionNumber(): number{
        return this._subdivisionNumber;
    }

    public get UserDrawnPoints(): Vector2[]{
        return this._userDrawnPoints;
    }

    public get CircleCenterPoint(): Vector2{
        return this._circleCenterPoint;
    }

    public set touchIndex(touchIndex: number){
        this._touchIndex = touchIndex;
    }

    public set shape(pointSet: PointSet){
        let ScaledUpPointSet = this.getScaledUpOriginal(pointSet);
        let ScaledDownPointSet = this.getScaledDownOriginal(pointSet);

        let subdividedOriginalPointset = this.subdividePointset(pointSet);
        let subdividedScaledUpPointset = this.subdividePointset(ScaledUpPointSet);
        let subdividedScaledDownPointset = this.subdividePointset(ScaledDownPointSet);

        this._originalShape = pointSet;
        this._originalShapeSubdivided = subdividedOriginalPointset;
        this._ScaledUpShape = subdividedScaledUpPointset;
        this._ScaledDownShape = subdividedScaledDownPointset;
    }

    public set subdivisionNumber(divisionNumber: number){
        this._subdivisionNumber = divisionNumber;
        this.shape = this._originalShape;
    }

    public set circleRadius(radius: number){
        if(radius < 0)
        radius = 0;

        this._circleSetRadius = radius;
    }

    public set circleMarginPercentage(percentage : number){
        if(percentage < 0)
        percentage = 0;

        this._circleMarginPercentage = percentage
    }
    
    public set shapeMarginPercentage(percentage : number){
        if(percentage < 0)
        percentage = 0;

        this._shapeMarginPercentage = percentage
        this.shape = this._originalShape;
    }

    constructor(){
        Input.touch.onTouchStartEvent.subscribe(x =>{
            if(x.touchIdentifier == this.touchIndex){
            this.clearUserDrawnPoints();
        }});
        Input.touch.onTouchEndEvent.subscribe(x => {
            if(x.touchIdentifier == this.touchIndex){
            this._isCircleDrawn = this.checkIfCircleIsDrawn();
            this._isShapeDrawn = this.checkIfShapeIsDrawn();
        }});
        Input.touch.onTouchMoveEvent.subscribe(x => {
            if(x.touchIdentifier == this.touchIndex){
            this.AddPointToUserDrawnPoints(x.clientPosition)
        }});
        
        this._userDrawnPoints = [];
        this._circleCenterPoint = new Vector2();
        this._circleAvarageRadius = 0;
        this._circleSetRadius = 0;

        //temp for testing
        //this.shape = new PointSet([new Vector2(2, 2.5), new Vector2(3.5, 0.5), new Vector2(0.5, 0.5)]);
        // let result = this.subdividePointset(new PointSet([new Vector2(2, 3), new Vector2(4, 0), new Vector2(0, 0)]));
        // console.log("after subDivision: ");
        // console.log( result);
        // let expectedResult = new PointSet([new Vector2(2, 3), new Vector2(3, 1.5), new Vector2(4, 0), new Vector2(2, 0), new Vector2(0, 0), new Vector2(1, 1.5)]);
        // console.log("expected subdivision: ");
        // console.log(expectedResult);

        // let result = this.subdividePointset(new PointSet([new Vector2(2, 2), new Vector2(3, 1), new Vector2(1, 1)]));
        // console.log("after subDivision: ");
        // console.log( result);
        // let expectedResult = new PointSet([new Vector2(2, 2), new Vector2(2.5, 1.5), new Vector2(3, 1), new Vector2(2, 1), new Vector2(1, 1), new Vector2(1.5, 1.5)]);
        // console.log("expected subdivision: ");
        // console.log(expectedResult);
    }

    public update(): void{

    }

    public lateUpdate(): void{
        this._isCircleDrawn = false;
    }

    public AddPointToUserDrawnPoints(point: Vector2): void{
        this._userDrawnPoints.push(point);
    }

    public clearUserDrawnPoints(): void{
        this._userDrawnPoints = [];
    }

    public checkIfCircleIsDrawn(): boolean{
        this.getCircleCenterPoint();
        let radius = 0;

        if(this._circleSetRadius == 0){
            this.getCircleAvarageRadius();
            radius = this._circleAvarageRadius;
        }
        if(this._circleSetRadius != 0){
            radius = this._circleSetRadius;
        }

        let result = false;

        for(let i = 0; i<this._userDrawnPoints.length; i++){
            result = this.checkIfPointIsValidForCircle(this._userDrawnPoints[i], radius);
            if(!result){
                return result;
            }
        }

        return result;
    }

    public checkIfShapeIsDrawn(): boolean{
        let result = false;
        let polygons : PolygonPointSet[] = [];
        let PolygonsCheck: [PolygonPointSet, boolean][] = [];

        polygons = this.createPolygons(this._ScaledDownShape, this._ScaledUpShape);
        polygons.forEach(x => { PolygonsCheck.push([x, false]);});

        for( let i = 0; i < this._userDrawnPoints.length; i++ ){
            for(let j = 0; j < polygons.length; j++){

                //console.log("test")
                let point = this._userDrawnPoints[i];
                result = this.checkIfWithinPolygon(point, polygons[j] )
                //console.log("point: " + (i+1) + " polygon: " + (j+1) + " result: " + result);
                if(result){
                    PolygonsCheck[j][1] = result;
                    //console.log("break out of check");
                    break;
                }
            }
            //console.log("next point");  
            if(!result){
                return result;
            }
        }
        
        for(let i = 0; i<PolygonsCheck.length; i++){
            result = PolygonsCheck[i][1];
            if(!result)
            break;
        }
        
        return result;
    }

    private getCircleCenterPoint(){

        let totalX = 0;
        let totalY = 0;

        this._userDrawnPoints.forEach(point =>{
            totalX += point.x;
            totalY += point.y;
        });

        let centerX = totalX / this._userDrawnPoints.length;
        let centery = totalY / this._userDrawnPoints.length;

        this._circleCenterPoint = new Vector2(centerX, centery);
    }

    private getCircleAvarageRadius(): void{
        let totalRadiusDistance = 0;
        let avarageRadiusDistance = 0;

        this._userDrawnPoints.forEach(point => {
            totalRadiusDistance += this._circleCenterPoint.distance(point);
        });

        avarageRadiusDistance = totalRadiusDistance / this._userDrawnPoints.length;

        this._circleAvarageRadius = avarageRadiusDistance;
    }

    private checkIfPointIsValidForCircle(point: Vector2, radius: number): boolean{
        let marginizedOuterRadius = ((radius / 100) * ( 100 + this._circleMarginPercentage));
        let marginizedInnerRadius = ((radius / 100) * ( 100 - this._circleMarginPercentage));
        let distance = this._circleCenterPoint.distance(point);

        if(marginizedInnerRadius <= distance && marginizedOuterRadius >= distance){
            return true;
        }
        return false;    
    }

    private getScaledUpOriginal(pointSet: PointSet): PointSet{     
        if(pointSet.vertices.length == 0)
            return pointSet;

        else{
            let scale = (100 + this._shapeMarginPercentage)/100;
            this._shape.transform.scale = new Vector2(scale, scale);
            let mesh = new Mesh(true);
            mesh.AddPointSet(pointSet);
            let newPointset : PointSet = new PointSet(mesh.VertexScaled(this._shape.transform)[0]);

            return newPointset;//new PointSet([new Vector2(2, 3.25), new Vector2(3, 1.5), new Vector2(4.25, 0), new Vector2(2, 0), new Vector2(-0.25, 0), new Vector2(1, 1.5)]); //temp hardcode for testing
        }
    }

    private getScaledDownOriginal(pointSet: PointSet){
        if(pointSet.vertices.length == 0)
        return pointSet;

        else{
            let scale = (100 + this._shapeMarginPercentage*-1)/100;
            this._shape.transform.scale = new Vector2(scale, scale);
            let mesh = new Mesh(true);
            mesh.AddPointSet(pointSet);
            let newPointset : PointSet = new PointSet(mesh.VertexScaled(this._shape.transform)[0]);
            return newPointset;//new PointSet([new Vector2(2, 2), new Vector2(2.5, 1.5), new Vector2(2.75, 1), new Vector2(2, 1), new Vector2(1.25, 1), new Vector2(1.5, 1.5)]); //temp hardcode for testing
        }
    }

    private subdividePointset(pointset: PointSet): PointSet{
        let newPointset = new PointSet();
        for(let i = 0; i < pointset.vertices.length; i++){
            let point1 = pointset.vertices[i];
            let point2 = pointset.vertices[i+1];

            if(i == pointset.vertices.length -1){
                point2 = pointset.vertices[0];
            }

            let offset: Vector2 = this.calculateSubdivisionOffset(point1, point2, this.subdivisionNumber);
            let tempNewPoints = this.getNewPointsAfterSubdivision(point1, offset, this.subdivisionNumber);

            tempNewPoints.forEach(x => {newPointset.vertices.push(x)});
        }
        return newPointset;
    }

    private calculateSubdivisionOffset(point1: Vector2, point2: Vector2, subdivisions:number): Vector2{
        let offset = new Vector2();

        offset.x = ((point1.x - point2.x) / subdivisions);
        offset.y = ((point1.y - point2.y) / subdivisions);

        return offset;
    }

    private getNewPointsAfterSubdivision(point: Vector2, offset: Vector2, subdivisions:number): Vector2[] {
        let newPoints: Vector2[] = [];
        let tempPoint = _.cloneDeep(point);

        newPoints.push(point);
        
        for(let i = 0; i < subdivisions -1; i++){

            tempPoint.x -= offset.x;
            tempPoint.y -= offset.y;
            newPoints.push(_.cloneDeep(tempPoint));
        }
        return newPoints;
    }

    public createPolygons(pointSetScaledDown: PointSet, pointSetScaledUp: PointSet): PolygonPointSet[]{
        let polygons : PolygonPointSet[] = [];
        for(let i = 0; i < this._originalShapeSubdivided.vertices.length; i++){
            let polygon: PolygonPointSet = new PolygonPointSet();

            polygon.addVertice(pointSetScaledDown.vertices[i]);
            polygon.addVertice(pointSetScaledUp.vertices[i]);

            if( i == this._originalShapeSubdivided.vertices.length -1){
                polygon.addVertice(pointSetScaledUp.vertices[0]);
                polygon.addVertice(pointSetScaledDown.vertices[0]);
            }
            else{
                polygon.addVertice(pointSetScaledUp.vertices[i+1]);
                polygon.addVertice(pointSetScaledDown.vertices[i+1]);
            }

            polygons.push(polygon);
        }
        return polygons
    }

    private checkIfWithinPolygon(position: Vector2, polygon: PolygonPointSet): boolean{
        let minXyAndMaxXY: number[] = this.getMinXYandMaxXY(polygon);
        let pointIsInBoundingBox = this.checkIfPointWithinBoundingBox(position, minXyAndMaxXY);
        let pointIsInPolygon = false;

        if(pointIsInBoundingBox){
            let pointSameAsVertice = this.checkIfPointSameAsVertice(position, polygon);
            //console.log("point same as vertice: " + pointSameAsVertice);
            if(!pointSameAsVertice){
                pointIsInPolygon = this.checkIfPointInPolygon(position, polygon);
                //console.log("point in Polygon: " + pointIsInPolygon);
            }
            else
                pointIsInPolygon = pointSameAsVertice;
        }

        return pointIsInPolygon;
    }

    private getMinXYandMaxXY(polygon: PolygonPointSet): number[]{
        let minXYAndMaxXy : number[] = [0, 0, 0, 0];

        for(let i = 0; i < polygon.vertices.length; i++){
            if(polygon.vertices[i].x < minXYAndMaxXy[0])
            minXYAndMaxXy[0] = polygon.vertices[i].x;

            if(polygon.vertices[i].x > minXYAndMaxXy[2])
            minXYAndMaxXy[2] = polygon.vertices[i].x;

            if(polygon.vertices[i].y < minXYAndMaxXy[1])
            minXYAndMaxXy[1] = polygon.vertices[i].y;

            if(polygon.vertices[i].y > minXYAndMaxXy[3])
            minXYAndMaxXy[3] = polygon.vertices[i].y;       
        }
        return minXYAndMaxXy;
    }

    private checkIfPointWithinBoundingBox(point: Vector2, polygonBoundingBox: number[]): boolean{
        if(point.x < polygonBoundingBox[0])
        return false;

        if(point.x > polygonBoundingBox[2])
        return false;

        if(point.y < polygonBoundingBox[1])
        return false;
        
        if(point.y > polygonBoundingBox[3])
        return false;

        return true;
    }

    private checkIfPointSameAsVertice(point: Vector2, polygon: PolygonPointSet): boolean{
        for(let i = 0; i < polygon.vertices.length; i++){
            if(polygon.vertices[i].equals(point)){
                return true;
            }     
        }

        return false;
    }

    private checkIfPointInPolygon(point: Vector2, polygon: PolygonPointSet): boolean{
        let isInside = false;

        for(let i = 0, j = polygon.vertices.length - 1; i < polygon.vertices.length; j = i++){
            let xyi = polygon.vertices[i];
            let xyj = polygon.vertices[j];

            let intersect = ((xyi.y > point.y) != (xyj.y > point.y)) && (point.x < ( xyj.x - xyi.x ) * (point.y - xyi.y) / ( xyj.y - xyi.y) + xyi.x)

            if(intersect){
                isInside = !isInside;
            }
        }

        return isInside;
    }



}
