import { SimpleEventDispatcher } from "strongly-typed-events";
import KeyboardData from "../input_data_classes/keyboard_data";
import _ from "lodash";
import { Key } from "ts-keycode-enum";

 class KeyboardHandler{
    private _onKeyDownEvent = new SimpleEventDispatcher<KeyboardData>();
    private _onKeyUpEvent = new SimpleEventDispatcher<KeyboardData>();
    private _onKeyPressedEvent = new SimpleEventDispatcher<KeyboardData>();

    private _keys = new Map<Key, KeyboardData>();
    private _KeysCache = new Map<Key, boolean>();
   
    public get onKeyDownEvent(): SimpleEventDispatcher<KeyboardData>{
        return this._onKeyDownEvent;
    }

    public get onKeyUpEvent(): SimpleEventDispatcher<KeyboardData>{
        return this._onKeyUpEvent;
    }

    public get onKeyPressedEvent(): SimpleEventDispatcher<KeyboardData>{
        return this._onKeyPressedEvent;
    }

    constructor(){
        this.addEventListners();
    }

    public update(){  
        if(this._keys.size != 0){
            this._keys.forEach((value: KeyboardData) => this._onKeyPressedEvent.dispatch(value));
        }
    }
    
    public lateUpdate(){
        this._KeysCache.clear();
    }

    public getKey(keyCode: Key): boolean{
        return this._keys.has(keyCode);
    }

    public getKeyDown(keyCode: Key): boolean{
        if(this._KeysCache.has(keyCode)){
            if(this._KeysCache.get(keyCode)!)
            return true;
        }
        return false;
    }

    public getKeyUp(keyCode: Key): boolean{
        if(this._KeysCache.has(keyCode)){
            if(!this._KeysCache.get(keyCode)!)
            return true;
        }
        return false;
    }

    public getKeys(): Map<Key, KeyboardData>{
        return _.cloneDeep(this._keys);
    }

    private addEventListners() :void {
        document.addEventListener(
            "keydown", 
            function(this: KeyboardHandler, event: KeyboardEvent){
                this.doOnKeyDown(event); 
            }.bind(this)
        );
        
        document.addEventListener(
            "keyup",
            function(this: KeyboardHandler, event: KeyboardEvent) {
                this.doOnKeyUp(event);
            }.bind(this)
        );

    }

    private doOnKeyDown(event: KeyboardEvent) : void {
        let dataObject = new KeyboardData(event.keyCode, event.ctrlKey, event.shiftKey, event.altKey, event.repeat, event.type);
       
        if (this._keys.has(dataObject.keyCode))
            return;

        this._keys.set(dataObject.keyCode, dataObject);
        this._KeysCache.set(dataObject.keyCode, true);
        
        this._onKeyDownEvent.dispatch(dataObject);     
    }

    private doOnKeyUp(event: KeyboardEvent) : void{
        let dataObject = new KeyboardData(event.keyCode, event.ctrlKey, event.shiftKey, event.altKey, event.repeat, event.type);

        if (!this._keys.has(dataObject.keyCode))
            return;

        this._keys.delete(dataObject.keyCode);
        this._KeysCache.set(dataObject.keyCode, false);

        this._onKeyUpEvent.dispatch(dataObject);
    }   
}

export default  KeyboardHandler;
