import MouseButton from "../input_enums/mouse_enum";
import { SimpleEventDispatcher } from "strongly-typed-events";
import MouseData from "../input_data_classes/mouse_data";
import _ from "lodash";
import { Vector2 } from "../../../../utility/math_extended/vector2";

class MouseHandler{
    private _onMouseDownEvent = new SimpleEventDispatcher<MouseData>();
    private _onMouseUp = new SimpleEventDispatcher<MouseData>();
    private _onMousePressed = new SimpleEventDispatcher<MouseData>();
    private _onMouseMove = new SimpleEventDispatcher<MouseData>();

    private _buttons = new Map<MouseButton,MouseData>();
    private _buttonsCache = new Map<MouseButton,boolean>();
    private _mousePosition: Vector2;

    public get onMouseDownEvent(): SimpleEventDispatcher<MouseData>{
        return this._onMouseDownEvent;
    }

    public get onMouseUpEvent(): SimpleEventDispatcher<MouseData>{
        return this._onMouseUp;
    }

    public get onMousePressedEvent(): SimpleEventDispatcher<MouseData>{
        return this._onMousePressed;
    }

    public get onMousemoveEvent(): SimpleEventDispatcher<MouseData>{
        return this._onMouseMove;
    }

    constructor(){
        this.addEventListners();
        this._mousePosition = new Vector2(0,0);
    }

    public update(){
        if(this._buttons.size != 0)
        this._buttons.forEach((value: MouseData) => this._onMousePressed.dispatch(value));
    }

    public lateUpdate(){
        this._buttonsCache.clear();
    }

    public getMouseButton(mouseButton: MouseButton): boolean{
        return this._buttons.has(mouseButton);
    }

    public getMouseButtonDown(mouseButton: MouseButton): boolean{
        if(this._buttonsCache.has(mouseButton)){
            if(this._buttonsCache.get(mouseButton)!)
            return true
        }

        return false;
    }

    public getMouseButtonUp(mouseButton: MouseButton): boolean{
        if(this._buttonsCache.has(mouseButton)){
            if(!this._buttonsCache.get(mouseButton)!)
            return true
        }
        return false;
    }

    public getMousePosition(): Vector2{
        return this._mousePosition;
    }

    public getMouseButtons(): Map<MouseButton,MouseData>{
        return _.cloneDeep(this._buttons);
    }

    private addEventListners() :void{
        document.addEventListener(
            "mousedown", 
            function(this:MouseHandler, event: MouseEvent){
                this.doOnMouseDown(event); 
            }.bind(this)
        );

        document.addEventListener(
            "mouseup", 
            function(this:MouseHandler, event: MouseEvent){
                this.doOnMouseUp(event); 
            }.bind(this)
        );

        document.addEventListener(
            "mousemove", 
            function(this:MouseHandler, event: MouseEvent){
                this.doOnMouseMove(event); 
            }.bind(this)
        );
    }

    private doOnMouseDown(event: MouseEvent) : void {
        if(this.doesButtonExistInButtonMap(event))
        return;

        let dataObject = this.createMouseDataObject(event);

        this._buttons.set(dataObject.mouseButton, dataObject);
        this._buttonsCache.set(dataObject.mouseButton, false);

        this._mousePosition = dataObject.clientPosition;

        this._onMouseDownEvent.dispatch(dataObject);
    }

    private doOnMouseUp(event: MouseEvent) : void {
        if(!this.doesButtonExistInButtonMap(event))
        return;

        let dataObject = this.createMouseDataObject(event);

        this._buttons.delete(dataObject.mouseButton);
        this._buttonsCache.set(dataObject.mouseButton, false);

        this._onMouseUp.dispatch(dataObject);
    }

    private doOnMouseMove(event: MouseEvent) :void{ 
        let dataObject = this.createMouseDataObject(event);

        this._mousePosition = dataObject.clientPosition;

        this._onMouseMove.dispatch(dataObject);
    }

    private doesButtonExistInButtonMap(event: MouseEvent):boolean{
        return this._buttons.has(event.which);
    }

    private createMouseDataObject(event: MouseEvent): MouseData{
        let screenPosition = new Vector2(event.screenX, event.screenY);
        let clientPosition = new Vector2(event.clientX, event.clientY);
        let dataObject = new MouseData(event.which, event.ctrlKey, event.shiftKey, event.altKey, screenPosition, clientPosition, event.type);

        return dataObject;
    }
}

export default MouseHandler;
