import * as PIXI from 'pixi.js';
import { PixiWorld } from '../pixi_world';
import { Application } from '../../../app';
import { IRenderable } from '../../core/components/rendering/renderers/Interfaces/irenderable1';
import { IHitable } from '../../core/components/rendering/renderers/Interfaces/ihitable';
import { CursorType } from '../../core/components/input/interaction/cursor_type';
import { TextItem } from '../../core/components/rendering/renderers/items/text_item';
import { GameObject } from '../../core/components/core/game_object';
import Camera from '../../core/components/rendering/camera';

export default class PixiTextRenderer implements IRenderable, IHitable {
  cursorType: CursorType = CursorType.DEFAULT;
  pointerMouse: boolean = false;
  world!: PixiWorld;
  items!: TextItem;
  gameObject!: GameObject;
  private _text!: PIXI.Text;

  constructor() {
    this.world = Application.instance.renderer.world as PixiWorld;
  }

  public SetItem(textItem: TextItem): PixiTextRenderer{
    this.items = textItem;
    return this;
  }

  Setup(): void {
    this._text = new PIXI.Text(this.items.text, this.items.GetStyle());
    this.Draw();
    this.world._app.stage.addChild(this._text);
  }

  Draw(): void {
    let transform = this.gameObject.transform;

    let position = transform.position;
    if(this.items.static){
      position = Camera.mainCamera.ViewToScreenSpace(position.subtract(Camera.mainCamera.gameObject.transform.position));
    }
    else{
      position = Camera.mainCamera.ViewToScreenSpace(position);
    }
    this._text.position = new PIXI.Point(position.x, position.y);
    this._text.angle = -transform.rotation;

    this._text.text = this.items.text;
    this._text.style = this.items.GetStyle();

    let scale = transform.scale.multiplyXY(Camera.mainCamera.zoom, Camera.mainCamera.zoom);
    this._text.zIndex = this.items.zIndex;
    this._text.anchor = new PIXI.Point(this.items.pivot.x, this.items.pivot.y);
    this._text.alpha = this.items.alpha;
    this._text.scale = new PIXI.Point(scale.x, scale.y);

    // width/height has to be set after everything else
    if(this.items.width != null){
      this._text.width = this.items.width;
    }
    if(this.items.height != null){
      this._text.height = this.items.height;
    }

    if(this.cursorType != CursorType.DEFAULT){
      this._text.interactive = true;
      this._text.cursor = CursorType[this.cursorType];
    }
  }

  CheckHit(positionX: number, positionY: number): boolean {
      let point:PIXI.Point = new PIXI.Point(positionX, positionY);
      return this._text.containsPoint(point);
  }

  SetCursor(cursor: CursorType): void {
    this.cursorType = cursor;
  }

  Destroy(): void {
    this._text.destroy();
  }
}
