import TransformHierarchy from "./transform_hierarchy";
import Transform from "./transform";
import { Matrix3x3 } from "../../../utility/math_extended/matrix";
import { Vector2 } from "../../../utility/math_extended/vector2";
import { Mathf } from "../../../utility/math_extended/mathf";

export default class HierarchyTransformChanges{
    private _hierarchy:TransformHierarchy;
    constructor(hierarchy:TransformHierarchy){
        this._hierarchy = hierarchy;
    }
    
    public SetLocalTransform(parent: Transform, ownTansform:Transform): void{
        // make a new matrix using the world transform values and "divide" by the parent matrix to get the new local values
        let worldmatrix = this.UpdateLocalMatrix(ownTansform, true);
        let inversParentMatrix = new Matrix3x3(parent.hierarchy.GetLocalToWorldMatrix().inverse);
        let matrix  = Matrix3x3.multiply(inversParentMatrix,worldmatrix).matrix;

        let transformvalues = this.DeconstructTransformMatrix(matrix);
        ownTansform.HierarchyUpdateLocalTransformValues(transformvalues.Position,transformvalues.Rotation,transformvalues.Scale,this._hierarchy);
    }
    
    public UpdateLocalMatrix(ownTransform: Transform,useWorldtransform = false): Matrix3x3{
        if(this._hierarchy.hasParent== false){
            this.ParentlessUpdateLocalTransform(ownTransform);
        }
        let worldtransform = this._hierarchy.hasParent && !useWorldtransform? false : true;
        return this.MakeTransformMatrix(worldtransform,ownTransform)
    }   
    
    public UpdateTransformFromParent(parentMatrix: Matrix3x3,localMatrix:Matrix3x3,ownTansform:Transform): void{
        let matrix = Matrix3x3.multiply(parentMatrix,localMatrix).matrix;

        let values = this.DeconstructTransformMatrix(matrix);

        ownTansform.HierarchyUpdateTransformValues(values.Position,values.Rotation,values.Scale,this._hierarchy);
        this.UpdateChildrenTransforms(new Matrix3x3(matrix));
    }
    
    public UpdateChildrenTransforms(parentMatrix: Matrix3x3): void{
        if(this._hierarchy.childCount > 0){
            this._hierarchy.children.forEach(child => {
                child.hierarchy.UpdateTransformFromParent(parentMatrix);
            });
        }
    }    

    public UpdateTransformValues(ownTansform: Transform):void{
        ownTansform.HierarchyUpdateTransformValues(ownTansform.localPosition,ownTansform.localRotation,ownTansform.localScale,this._hierarchy);
    }

    private MakeTransformMatrix(useWorldtransform: boolean,ownTansform: Transform): Matrix3x3{

        let scale    = useWorldtransform? ownTansform.scale    : ownTansform.localScale;
        let rotation = useWorldtransform? ownTansform.rotation : ownTansform.localRotation;
        let position = useWorldtransform? ownTansform.position : ownTansform.localPosition;

        let localScaleMatrix    = Matrix3x3.scale(scale);
        let localRotationMatrix = Matrix3x3.rotate(rotation);
        let localPositionMatrix = Matrix3x3.translate(position);

        let tempMatrix = Matrix3x3.multiply(localRotationMatrix,localScaleMatrix);
        return Matrix3x3.multiply(localPositionMatrix,tempMatrix);
    }

    private ParentlessUpdateLocalTransform(ownTansform: Transform):void{
        ownTansform.HierarchyUpdateLocalTransformValues(ownTansform.position,ownTansform.rotation,ownTansform.scale,this._hierarchy);
    }

    private DeconstructTransformMatrix(matrix:number[][]):TransformValues{
        let positionX = matrix[0][2];
        let PositionY = matrix[1][2];

        let scalex = new Vector2(matrix[0][0],matrix[1][0]).magnitude();
        let scaley = new Vector2(matrix[0][1],matrix[1][1]).magnitude();

        let rotation:number
        if(Math.atan2(-matrix[1][0],matrix[0][0]) === Math.atan2(matrix[0][1],matrix[1][1])){
            rotation = Mathf.radiansToDegrees(Math.atan2(-matrix[1][0],matrix[0][0]))* -1;
        } else{
            rotation = Mathf.radiansToDegrees(Math.atan2(matrix[0][1],matrix[1][1])) * -1;
        }

        let values: TransformValues = {Position : new Vector2(positionX,PositionY),Rotation: rotation,Scale:new Vector2(scalex,scaley)}
        return values;
    }

}

interface TransformValues{
     Position: Vector2;
     Rotation: number;
     Scale: Vector2
}
