import Transform from "../../core/transform";
import { PointSet } from "../point_sets/point_set";
import Camera from "../../rendering/camera";
import { PolygonPointSet } from "../point_sets/polygon_point_set";
import { PointSetTypes } from "../enums/pointSet_types";
import { Vector2 } from "../../../../utility/math_extended/vector2";
import { Matrix3x3 } from "../../../../utility/math_extended/matrix";

export class Mesh  {
  private _pointSets: PointSet[] = Array<PointSet>();
  public shapeType!: PointSetTypes;

  private _vertexScale:boolean;
  private _vertexNormals:Map<Vector2,Vector2>
  private _vertexScales:Map<Vector2,number>

  constructor(vertexScale = false,points?: PointSet[],clockWise:boolean = true) {
    this._pointSets = points != null ? points : Array<PointSet>();
    this._vertexScale = vertexScale;
    this._vertexNormals = new Map();
    this._vertexScales = new Map();
    if(vertexScale && points) this._vertexNormals = this.CreateVectorNormals(points[0].vertices,clockWise)
  }
  
  public get PointSets(): PointSet[]{
    return this._pointSets;
  };

  public AddPointSet(points:PointSet,clockWise:boolean = true){
    this._pointSets.push(points);
    if(this._vertexScale){
      this._vertexNormals = this.CreateVectorNormals(points.vertices,clockWise);
    }
  }

  public UpdateNormals(){
    this._vertexNormals = this.CreateVectorNormals(this._pointSets[0].vertices,true);
  }
  public GetVertexNormals():Map<Vector2,Vector2>{
    return this._vertexNormals
  }

  public GetRelativePoints(transform: Transform, ignoreCamera: boolean = false): Vector2[][] {
    let relativePointSets: Vector2[][] = [[]];
    if(this._vertexScale && this._vertexNormals.size > 0){
       relativePointSets = this.VertexScaled(transform)
             
    }
    for (var q = 0; q < this._pointSets.length; q++) {
      for (let w = 0; w < this._pointSets[q].vertices.length; w++) {
        let element = this._pointSets[q].vertices[w];
        let transformedPoint = transform.TransformPoint(element);
        
        if(this._vertexScale && this._vertexNormals.size > 0){
          relativePointSets[q][w] = Camera.mainCamera.WorldToScreenSpace(relativePointSets[q][w])
        }
        else{
          if(ignoreCamera){
            transformedPoint = transformedPoint.subtract(Camera.mainCamera.WorldToScreenSpace(Camera.mainCamera.gameObject.transform.position).subtract(Camera.mainCamera.WorldToScreenSpace(new Vector2())));
          }
          relativePointSets[q].push(transformedPoint);
        }

      }
    }
    return relativePointSets;
  }

  public GetScaledPoints(scale: Vector2): Vector2[][] {
    
    let relativePointSets: Vector2[][] = [[]];
    let scaleMatrix = Matrix3x3.scale(scale);

    for (var q = 0; q < this._pointSets.length; q++) {
      for (let w = 0; w < this._pointSets[q].vertices.length; w++) {
        let element = this._pointSets[q].vertices[w];
        element = scaleMatrix.multiplyByVector(element);
        relativePointSets[q].push(element);
      }
    }
    return relativePointSets;
  }

  public static Circle(size: Vector2 = new Vector2(1, 1), offset: Vector2 = new Vector2(), roughness: number = 15){
    let circle: Mesh = new Mesh();
    circle.AddPointSet(PolygonPointSet.CreateCircle(size, new Vector2(offset.x, offset.y), roughness));
    return circle;
  }

  public static Box(size: Vector2 = new Vector2(1, 1), offset: Vector2 = new Vector2()){
    let box: Mesh = new Mesh();
    box._pointSets.push(PolygonPointSet.CreateBox(size, new Vector2(offset.x, offset.y)));
    return box;
  }


  public VertexScaled(transform: Transform):Vector2[][]{
    let relativePointSets: Vector2[][] = [[]];

    for (var q = 0; q < this._pointSets.length; q++) {
      for (let w = 0; w < this._pointSets[q].vertices.length; w++) {
        let vertex = this._pointSets[q].vertices[w];
        let dot = this._vertexScales.get(vertex)!;
        let ScaledNormal = this._vertexNormals.get(vertex)!.multiplyXY(dot,dot)
        let newVertex = vertex.add(ScaledNormal.multiply(transform.scale.subtract(new Vector2(1,1))))

        newVertex = Matrix3x3.multiplyVector(Matrix3x3.rotate(transform.rotation),newVertex);
        newVertex = newVertex.add(transform.position)
        relativePointSets[q].push(newVertex);
      }
    }
    return relativePointSets
  }

  private CreateVectorNormals(vertices:Vector2[],clockWise:boolean):Map<Vector2,Vector2>{
    let normals: Map<Vector2,Vector2> = new Map();
    
    for(let i = 0;i<vertices.length;i++){
      let previous:number = i -1
      let next:number = i + 1
      if(i === 0){ previous = vertices.length -1;}
      else if (i === vertices.length - 1){next = 0;}

      let vectcurrent = vertices[next].subtract(vertices[i]);
      let vectPrev = vertices[i].subtract(vertices[previous]);
      
      vectcurrent = vectcurrent.normalized()
      vectPrev = vectPrev.normalized();
      let normalcurrent:Vector2
      let normalPrevious:Vector2

      if(clockWise){
        normalcurrent = new Vector2(-vectcurrent.y,vectcurrent.x);
        normalPrevious = new Vector2(-vectPrev.y,vectPrev.x);
      }
      else{
        normalcurrent = new Vector2(vectcurrent.y,-vectcurrent.x);
        normalPrevious = new Vector2(vectPrev.y,-vectPrev.x);
      }
      


      let vertexNormal = new Vector2()
      
      
        
        let dot = Vector2.dot(normalPrevious,normalcurrent);
        
        
        
        if(dot <= 0){
          dot = 1- dot
        }
        
        this._vertexScales.set(vertices[i],dot)
        vertexNormal = (normalcurrent.add(normalPrevious)).normalized();
        normals.set(vertices[i],vertexNormal);
  
    }
    return normals;
  }

}
