import TransformHierarchy from './transform_hierarchy';
import Camera from '../rendering/camera';
import { GameObject } from './game_object';
import { TransformData } from '../assetloader/json/data_classes/component_data/transform_data';
import { RigidBody } from '../physics/rigid_body';
import { Component } from './component';
import { Vector2 } from '../../../utility/math_extended/vector2';
import { Mathf } from '../../../utility/math_extended/mathf';
import { Matrix3x3 } from '../../../utility/math_extended/matrix';

export default class Transform extends Component {
  
  public data: TransformData;
  private _hierarchy!: TransformHierarchy;

  constructor(gameObject: GameObject, componentData: TransformData = new TransformData(gameObject.name + "TransformData")) {
    super(gameObject);
    this.data = componentData.GetCopy();
    this._hierarchy = new TransformHierarchy(this);
    
  }

  /// GETTERS/SETTERS

  public get hierarchy(){
    return this._hierarchy;
  }

  /**
   * Getter parent
   * @return {Transform}
   */
  public get parent(): Transform  {
    return this._hierarchy.GetParent();
  }

  /**
   * Setter parent
   * @param {Transform} value
   */
  public set parent(value: Transform) {
    this._hierarchy.SetParent(value);
  }

  public get hasParent():boolean{
    return this._hierarchy.hasParent;
  }

  public get children():Transform[]{
    return this._hierarchy.children;
  }

  /**
   * Getter position
   * @return {number}
   */
  public get position(): Vector2 {
    return this.data.position;
  }

  /**
   * Setter position
   * @param {number} value
   */
  public set position(value: Vector2) {
    this.data.position = value;
    this.hierarchy.OnTransformValueChange();
  }

  /**
   * Getter position
   * @return {number}
   */
  public get localPosition(): Vector2 {
    return this.data.localPosition;
  }

  /**
   * Setter position
   * @param {number} value
   */
  public set localPosition(value: Vector2) {
    this.data.localPosition = value;
    this._hierarchy.OnLocalTransformValueChange();
  }

  /**
   * Getter position
   * @return {number}
   */
  public get rotation(): number {
    return this.data.rotation;
  }

  /**
   * Setter position
   * @param {number} value
   */
  public set rotation(value: number) {
    this.data.rotation = value;
    this.hierarchy.OnTransformValueChange();
  }

  /**
   * Getter localRotation
   * @return {number}
   */
  public get localRotation(): number {
    return this.data.localRotation;
  }

  /**
   * Setter localRotation
   * @param {number} value
   */
  public set localRotation(value: number) {
    this.data.localRotation = value;
    this._hierarchy.OnLocalTransformValueChange();
  }

  /**
   * Setter position
   * @param {number} value
   */
  public set scale(value: Vector2) {
    this.data.scale = value;
    this.hierarchy.OnTransformValueChange();
  }

  /**
   * Getter position
   * @return {number}
   */
  public get scale(): Vector2 {
    return this.data.scale;
  }

  /**
   * Setter position
   * @param {number} value
   */
  public set localScale(value: Vector2) {
    this.data.localScale = value;
    this._hierarchy.OnLocalTransformValueChange();
  }

  /**
   * Getter position
   * @return {number}
   */
  public get localScale(): Vector2 {
    return this.data.localScale;
  }

  /**
   * Getter rootParent
   * @return {Transform}
   */
  public get rootParent(): Transform {
    return this.hierarchy.rootParent;
  }

  /**
   * Setter rootParent
   * @param {Transform} value
   */
  public set rootParent(value: Transform) {
    this.hierarchy.rootParent = value;
  }

  /**
   * Getter childCount
   * @return {number}
   */
  public get childCount(): number {
    return this.hierarchy.childCount;
  }

  /**
   * Getter hierarchyCount
   * @return {number}
   */
  // do we truly need this
  public get hierarchyCount(): number {
    return this.data.hierarchyCount;
  }

  /**
   * Getter hierarchyCapacity
   * @return {number}
   */
  public get hierarchyCapacity(): number {
    return this.data.hierarchyCapacity;
  }

  /**
   * Setter hierarchyCapacity
   * @param {number} value
   */
  public set hierarchyCapacity(value: number) {
    this.data.hierarchyCapacity = value;
  }

  public SetParent(parent: Transform):void {
    this._hierarchy.SetParent(parent);
  }

  /// FUNCTIONS
  public DetachChildren():void {
    this.hierarchy.RemoveAllChildren();
  }
  
  public Find(childName: string):Transform | undefined {
    return this.hierarchy.FindChildByName(childName);
  }

  public GetChild(childNumber: number):Transform | undefined {
     return this.hierarchy.GetChildByIndex(childNumber);
  }

  public GetSiblingIndex(): number {
    return this.hierarchy.GetSiblingIndex();
  }

  public InverseTransformDirection() {
    // Inverses transform direction
  }

  public InversePointDirection() {
    // Inverses point direction
  }

  public InverseTransformVector() {
    // Inverses transform direction
  }

  public IsChildOf(transform: Transform): boolean {
    return this.hierarchy.IsChildOf(transform);
  }

  public LookAt(target: Vector2, up: Vector2 = Vector2.up()) {
    let relativePosition = target.subtract(this.position);
    let radians = Math.atan2(up.x * relativePosition.y - up.y * relativePosition.x, up.x * relativePosition.x + up.y * relativePosition.y); // returns angle in radians (both directions)
    let rigidBody = this.gameObject.GetComponent("RigidBody") as RigidBody;
    if(rigidBody != null){
      rigidBody.SetRotation(radians);
    }
    else{
      this.rotation = Mathf.radiansToDegrees(radians);
    }
  }

  public TransformPoint(point: Vector2){
    let rotationMatrix = Matrix3x3.rotate(this.rotation);
    let scaleMatrix = Matrix3x3.scale(this.scale);
    let totalmatrix = Matrix3x3.multiply(rotationMatrix,scaleMatrix);

    let newPoint = totalmatrix.multiplyByVector(point);
    newPoint = newPoint.add(this.position);
    newPoint = Camera.mainCamera.ViewToScreenSpace(newPoint);
    return newPoint;
  }

  public Rotate(angle: number):void {
    this.data.localRotation += angle;
    this._hierarchy.OnLocalTransformValueChange();
  }

  public SetRotation(angle: number):void {
    this.data.localRotation = angle;
    this._hierarchy.OnLocalTransformValueChange();
  }

  public SetSiblingIndex(index: number):void {
    this.hierarchy.SetSiblingIndex(index);
  }

  public SetAsFirstSibling():void {
    this.hierarchy.SetSiblingIndex(0);
  }

  public SetLastSibling():void {
    this.hierarchy.SetSiblingIndex(this.childCount -1);
  }

  public AddChild(child: Transform):void{
    this._hierarchy.AddChild(child);
  }

  public RemoveParent(): void{
    this.parent.hierarchy.RemoveChild(this);
    this._hierarchy.RemoveParent();
  }
  
  public RemoveChildAtIndex(index: number): void{
    this.children[index].hierarchy.RemoveParent();
    this._hierarchy.RemoveChildAtIndex(index);
  }

  // only to be used by the transform hierarchy
  public HierarchyUpdateTransformValues(position:Vector2,rotation:number,scale:Vector2,hierarchy:TransformHierarchy):void{
    if(hierarchy === this._hierarchy){
      this.data.position = position;
      this.data.scale = scale;
      this.data.rotation = rotation;
    }
  }

  public HierarchyUpdateLocalTransformValues(position:Vector2,rotation:number,scale:Vector2,hierarchy:TransformHierarchy):void{
    if(hierarchy === this._hierarchy){
      this.data.localPosition = position;
      this.data.localScale = scale;
      this.data.localRotation = rotation;
      }
    }
  }
