import { PixiWorld } from '../pixi_world';
import * as PIXI from 'pixi.js';
import { Application } from '../../../app';
import { IRenderable } from '../../core/components/rendering/renderers/Interfaces/irenderable1';
import { CursorType } from '../../core/components/input/interaction/cursor_type';
import { LineItem } from '../../core/components/rendering/renderers/items/line_item';
import { GameObject } from '../../core/components/core/game_object';
import Camera from '../../core/components/rendering/camera';
import { Vector2 } from '../../utility/math_extended/vector2';

export default class PixiLineRenderer implements IRenderable {
  cursorType: CursorType = CursorType.DEFAULT;
  world!: PixiWorld;
  items!: LineItem[];
  gameObject!: GameObject;
  protected _graph: PIXI.Graphics;

  constructor(lines?: LineItem[]) {
    this.items = lines != null ? lines : new Array<LineItem>();
    this.world = Application.instance.renderer.world as PixiWorld;
    this._graph = new PIXI.Graphics()
  }

  SetItem(lines: LineItem[]):PixiLineRenderer{
    this.items = lines;
    return this;
  }

  Setup(): void {
    this.Draw();
    this.world._app.stage.addChild(this._graph);
  }

  Draw(): void {
    this._graph.clear();
    let transform = this.gameObject.transform;
    let position = transform.position;
    
    if(this.items[0].static){
      position = Camera.mainCamera.ViewToScreenSpace(position.subtract(Camera.mainCamera.gameObject.transform.position));
    }
    else{
      position = Camera.mainCamera.ViewToScreenSpace(position);
    }
    this._graph.position = new PIXI.Point(position.x, position.y);
    this._graph.angle = -this.gameObject.transform.rotation;

    for(let i = 0; i < this.items.length; i++){
      this._graph.lineStyle(this.items[i].lineWidth, this.items[i].color.HexCode, this.items[i].alpha, this.items[i].alignment);
        // render lines
        let points:Vector2[] = this.items[i].linePoints.vertices;
        if(points.length > 1){
            this._graph.moveTo(points[0].x, points[0].y)
            for (let j = 1; j < points.length; j++) {
                this._graph.lineTo(points[j].x, points[j].y);
                this._graph.zIndex = this.items[i].zIndex;
            }
        }
    }
  }

  Destroy(): void {
    this._graph.destroy();
  }
}
