import HierarchyTransformChanges from "./hierarchy_transform_changes"
import Transform from "./transform";
import { Matrix3x3 } from "../../../utility/math_extended/matrix";

export default class TransformHierarchy {
    private _parent!: Transform;
    private _rootParent!: Transform;
    private _children: Transform[];
    private _ownTransform!: Transform;
    private _localTransformMatrix!: Matrix3x3;
    private _hasParent: boolean;
    private _hierarchyTransformChanges:HierarchyTransformChanges;

    public constructor(ownTransform: Transform){
        this._ownTransform = ownTransform;
        this._children = [];
        this._hasParent = false;
        this._hierarchyTransformChanges = new HierarchyTransformChanges(this); 

        this.UpdateLocalTransformMatrix();
    }
    
    public  GetParent(): Transform {
        return this._parent;
    }

    // error catch if parent is self
    public SetParent(parent: Transform): void{
        this._parent = parent;
        this._hasParent = true;
        this.rootParent = parent.hierarchy.hasParent?parent.rootParent:parent;

        this._parent.hierarchy.UpdateHierarchyFromChild(this._ownTransform);
        this._hierarchyTransformChanges.SetLocalTransform(parent,this._ownTransform);
        this.UpdateLocalTransformMatrix();
    }

    public get hasParent():boolean{
        return this._hasParent;
    }

    public set rootParent(rootParent: Transform){
        this._rootParent = rootParent;
        if(this.childCount > 0){
            this.children.forEach(child => {
                child.rootParent = rootParent;
            });
        }
    }

    public get rootParent():Transform{
        return this._rootParent;
    }

    public get children(): Transform[]{
        return this._children;
    }

    private get localTransformMatrix(): Matrix3x3{
        return this._localTransformMatrix;
    }

    public get childCount(): number{
        return this._children.length;
    }

    public AddChild(child: Transform): void{
        this._children.push(child);
        child.hierarchy.UpdateHierarchyFromParent(this._ownTransform);
    }

    public RemoveChildAtIndex(index: number): void{
        this._children.splice(index,1);
    }

    public RemoveChild(child: Transform): void{
        let index = this._children.indexOf(child);
        if(index != -1){
            this.RemoveChildAtIndex(index);
        }
    }

    public RemoveAllChildren(){
         this._children.forEach(child => {
            child.RemoveParent();
        });
        this._children = [];
    }

    public RemoveParent(): void{
        if(this._hasParent){
            this._hasParent = false;
            this.UpdateLocalTransformMatrix();
        }
    }

    public GetChildByIndex(index: number): Transform | undefined{
        if(this.childCount > 0){
        return this._children[index];
        }
        return undefined;
    }

    public FindChildByName(name: string): Transform | undefined{
        for(let i = 0;i < this.childCount; i++){
            if(this.children[i].gameObject.name == name){
              return this.GetChildByIndex(i);
            }
          }
          return undefined;
    }

    public GetSiblingIndex():number{
        if(this.hasParent){
        return this._parent.children.indexOf(this._ownTransform);
        }
        return -1;
    }

    public SetSiblingIndex(index: number):void{
        if(this.hasParent){
        this._parent.children.splice(this.GetSiblingIndex(),1);
        this._parent.children.splice(index,0,this._ownTransform);
        }
    }

    // follows unity; the unity documentation says returns true if self, a child of and a child of child
    public IsChildOf(parent: Transform): boolean{
        if(this._ownTransform == parent) return true;
        if(parent.childCount <= 0) return false;
        for(let child of parent.children){
            if(child == this._ownTransform) return true;
            if( this._ownTransform.IsChildOf(child)){
                return true;
            }
        }
         return false;
    }
    
    public GetLocalToWorldMatrix(): Matrix3x3{
        this.UpdateLocalTransformMatrix();
        if(this.hasParent){
            return Matrix3x3.multiply(this._parent.hierarchy.GetLocalToWorldMatrix(),this._localTransformMatrix);
        }
        else{
            return this._localTransformMatrix;
        }
    }

    public OnLocalTransformValueChange():void{
        this.UpdateLocalTransformMatrix();
        if(this.hasParent){
            this._hierarchyTransformChanges.UpdateTransformFromParent(this._parent.hierarchy.GetLocalToWorldMatrix(),this.localTransformMatrix,this._ownTransform);
        }
        else{
            this._hierarchyTransformChanges.UpdateTransformValues(this._ownTransform);
            this.UpdateLocalTransformMatrix();
            this._hierarchyTransformChanges.UpdateChildrenTransforms(this.localTransformMatrix);
        }
    }

    public OnTransformValueChange(): void{
        let matrix = new Matrix3x3(Matrix3x3.identity);
        if(this.hasParent){
            this._hierarchyTransformChanges.SetLocalTransform(this._parent,this._ownTransform);
            matrix = this.GetLocalToWorldMatrix();
          }
          else{
            this.UpdateLocalTransformMatrix();
            matrix = this.localTransformMatrix
        }
          this._hierarchyTransformChanges.UpdateChildrenTransforms(matrix);
    }

    public UpdateTransformFromParent(parent:Matrix3x3):void{
        this._hierarchyTransformChanges.UpdateTransformFromParent(parent,this._localTransformMatrix,this._ownTransform);
    }

    private UpdateLocalTransformMatrix():void{
        this._localTransformMatrix  = this._hierarchyTransformChanges.UpdateLocalMatrix(this._ownTransform);
    }


    private UpdateHierarchyFromParent(parentTransform: Transform): void{
        this._parent = parentTransform;
        this.rootParent = this._parent.hierarchy.hasParent?this._parent.rootParent:this._parent
        this._hasParent = true;
        this._hierarchyTransformChanges.SetLocalTransform(parentTransform,this._ownTransform);
    }

    private UpdateHierarchyFromChild(childTransform: Transform): void{
        this._children.push(childTransform);
    }
}
