import * as PIXI from 'pixi.js';
import { PixiWorld } from '../pixi_world';
import { Application } from '../../../app';
import { IRenderable } from '../../core/components/rendering/renderers/Interfaces/irenderable1';
import { IHitable } from '../../core/components/rendering/renderers/Interfaces/ihitable';
import { CursorType } from '../../core/components/input/interaction/cursor_type';
import { RenderMeshItem } from '../../core/components/rendering/renderers/items/render_mesh_item';
import { GameObject } from '../../core/components/core/game_object';
import { Vector2 } from '../../utility/math_extended/vector2';

export default class PixiShapeRenderer implements IRenderable, IHitable {
  cursorType: CursorType = CursorType.DEFAULT;
  world!: PixiWorld;
  items!: RenderMeshItem[];
  gameObject!: GameObject;
  protected _graph: PIXI.Graphics;

  constructor(shapes?: RenderMeshItem[]) {
    this.items = shapes != null ? shapes : new Array<RenderMeshItem>();
    this.world = Application.instance.renderer.world as PixiWorld;
    this._graph = new PIXI.Graphics()
  }

  SetItem(shapes: RenderMeshItem[]):PixiShapeRenderer{
    this.items = shapes;
    return this;
  }

  Setup(): void {
    this.Draw();
    this.world._app.stage.addChild(this._graph);
  }

  Draw(): void {
    this._graph.clear();
    for(let i = 0; i < this.items.length; i++){
      var points:Vector2[][] = this.items[i].mesh.GetRelativePoints(this.gameObject.transform, this.items[i].static);
      for (var q = 0; q < points.length; q++) {
        let vertices = points[q];
  
        let pointsToDraw: PIXI.Point[] = [];
        for (let index = 0; index < vertices.length; index++) {
          pointsToDraw.push(new PIXI.Point(vertices[index].x, vertices[index].y));
        }
          this._graph.alpha = this.items[i].alpha;
          this._graph.beginFill(this.items[i].col);
          this._graph.drawPolygon(pointsToDraw);
          //this._graph.pivot = new PIXI.Point(this.gameObject.transform.position.x + (this._graph.width * this.items[i].pivot.x), this.gameObject.transform.position.y + (this._graph.height * this.items[i].pivot.y));
          this._graph.zIndex = this.items[i].zIndex;
          if(this.cursorType != CursorType.DEFAULT){
            this._graph.interactive = true;
            this._graph.cursor = CursorType[this.cursorType];
          }
          this._graph.endFill();
          
          if(this.items[i].width != null){
            this._graph.width = this.items[i].width;
          }
          if(this.items[i].height != null){
            this._graph.height = this.items[i].height;
          }
      }
    }
  }

  CheckHit(positionX: number, positionY: number): boolean {
    let point:PIXI.Point = new PIXI.Point(positionX, positionY);
    return this._graph.containsPoint(point);
  }

  SetCursor(cursor: CursorType): void {
    this.cursorType = cursor;
  }

  Destroy(): void {
    this._graph.destroy();
  }
}
