import { SimpleEventDispatcher } from "strongly-typed-events";
import GamepadData from "../input_data_classes/gamepad_data";
import { JoyStick, ControllerButton } from "../input_enums/controller_enum";
import _ from "lodash";
import { Vector2 } from "../../../../utility/math_extended/vector2";
import { Mathf } from "../../../../utility/math_extended/mathf";

class GamepadHandler{
    private _onGamepadConnected = new SimpleEventDispatcher<GamepadEvent>();
    private _onGamepadDisconnected = new SimpleEventDispatcher<GamepadEvent>();
    private _onGamepadButtonPressed = new SimpleEventDispatcher<GamepadData>();
    private _onGamepadButtonDown = new SimpleEventDispatcher<GamepadData>();
    private _onGamepadButtonUp = new SimpleEventDispatcher<GamepadData>();
    private _onGamepadTriggerAxisChanged = new SimpleEventDispatcher<GamepadData>();
    private _onGamepadJoystickAxis = new SimpleEventDispatcher<GamepadData>();

    private _gamepads: Gamepad[] = [];
    private _buttonsCache =  new Map<number, GamepadButton[]>();
    private _buttonsStatus = new Map<number, GamepadButton[]>();

    private _leftJoystickAxis = new Map<number, Vector2>();
    private _rightJoystickAxis = new Map<number, Vector2>();

    private _deadZone: number;
    private _deadZoneMin: number;
    private _deadZoneMax: number;

    public get onGamepadConnectedEvent(): SimpleEventDispatcher<GamepadEvent>{
        return this._onGamepadConnected;
    }

    public get onGamepadDisconnectedEvent(): SimpleEventDispatcher<GamepadEvent>{
        return this._onGamepadDisconnected;
    }

    public get onGamepadButtonPressed(): SimpleEventDispatcher<GamepadData>{
        return this._onGamepadButtonPressed;
    }

    public get onGamepadButtonDown(): SimpleEventDispatcher<GamepadData>{
        return this._onGamepadButtonDown;
    }

    public get onGamepadButtonUp(): SimpleEventDispatcher<GamepadData>{
        return this._onGamepadButtonUp;
    }

    public get onGamepadTriggerAxisChanged(): SimpleEventDispatcher<GamepadData>{
        return this._onGamepadTriggerAxisChanged;
    }

    public get onGamepadJoystickAxis(): SimpleEventDispatcher<GamepadData>{
        return this._onGamepadJoystickAxis;
    }

    public get deadZone():number 
    {
        return this._deadZone;
    }

    public set deadZone(value : number) {
        value = Math.abs(value)
        value = Mathf.clamp(value, this._deadZoneMin , this._deadZoneMax);
        this._deadZone = value;
    }

    constructor(){
        this._deadZone = 0.1;
        this._deadZoneMin = 0.1;
        this._deadZoneMax = 0.9;
        this.addEventListners();
    }

    public update(){
        let gamepads = navigator.getGamepads ? navigator.getGamepads() : [];
        
        this.clearAndSetCache();
        //temp map of all pressed buttons on every connected controller by index
        let pressed = new Map<number, GamepadButton[]>(); 
        let leftStick = new Map<number, Vector2>();
        let rightStick = new Map<number, Vector2>();
    
        for(let i = 0; i<gamepads.length; i++){ //loop trough all connected controllers and check if there actually is one
            if(gamepads[i] != null){      
                let Buttons: GamepadButton[] = []; // temp array to store all pressed buttons of a specifi controller 
                for(let j = 0; j < gamepads[i]!.buttons.length; j++){ //loop over all buttons on controller  
                    Buttons.push(gamepads[i]!.buttons[j]);  //add button to temp array
                }

                leftStick.set(i, new Vector2(gamepads[i]!.axes[0], gamepads[i]!.axes[1]*-1));
                rightStick.set(i, new Vector2(gamepads[i]!.axes[2], gamepads[i]!.axes[3]*-1));
                pressed.set(gamepads[i]!.index,Buttons);
            }
        }
        this._buttonsStatus = new Map(pressed);
        this._leftJoystickAxis = new Map(leftStick);
        this._rightJoystickAxis = new Map(rightStick);

        this.dispatchEvents();
    }

    public getButton(buttonIndex: ControllerButton , gamepadIndex: number): boolean{
        if(this.isGamepadIndexValid(gamepadIndex)){ 
            if(this._buttonsStatus.has(gamepadIndex)){
                let tempButtonStatus = this._buttonsStatus.get(gamepadIndex)!;
                return tempButtonStatus[buttonIndex].pressed;
            }
        }
        return false;
    }

    public getButtonDown(buttonIndex: ControllerButton , gamepadIndex: number): boolean{
        if(this.isGamepadIndexValid(gamepadIndex)){ 
            if(this._buttonsCache.has(gamepadIndex) && this._buttonsStatus.has(gamepadIndex)){
                let tempButtonStatus = this._buttonsStatus.get(gamepadIndex)!;
                let tempButtonCache = this._buttonsCache.get(gamepadIndex)!;
                if(!tempButtonCache[buttonIndex].pressed && tempButtonStatus[buttonIndex].pressed)
                return true;
            }
        }
        return false;
    }

    public getButtonUp(buttonIndex: ControllerButton , gamepadIndex: number): boolean{
        if(this.isGamepadIndexValid(gamepadIndex)){
            if(this._buttonsCache.has(gamepadIndex) && this._buttonsStatus.has(gamepadIndex)){
                let tempButtonCache = this._buttonsCache.get(gamepadIndex)!;
                let tempButtonStatus = this._buttonsStatus.get(gamepadIndex)!;
                if(tempButtonCache[buttonIndex].pressed && !tempButtonStatus[buttonIndex].pressed){
                    return true;
                }
            }
        }
        return false;
    }

    public getButtonValue(buttonIndex: ControllerButton , gamepadIndex: number):number {
        if(this.isGamepadIndexValid(gamepadIndex)){
            if(this._buttonsStatus.has(gamepadIndex)){
                let tempButtonStatus = this._buttonsStatus.get(gamepadIndex)!;
                return tempButtonStatus[buttonIndex].value;
            }
        }
        return 0;
    }

    public getJoystickAxis(joystick: JoyStick, gamepadIndex: number): Vector2{
        let value = new Vector2;

        if(joystick == JoyStick.LEFTSTICK){
            if(this._leftJoystickAxis.has(gamepadIndex)){
                value = this._leftJoystickAxis.get(gamepadIndex)!;
            }
        }

        if(joystick == JoyStick.RIGHTSTICK){
            if(this._rightJoystickAxis.has(gamepadIndex)){
                value = this._rightJoystickAxis.get(gamepadIndex)!;
            }
        }
        
        return this.CheckDeadZone(value);
    }

    private CheckDeadZone(value: Vector2):Vector2{
        
        if(!(value.x < (0 - this._deadZone)) && !(value.x > (0 + this._deadZone)) )
        value.x = 0;

        if(!(value.y < (0 - this._deadZone))&& !(value.y > (0 + this._deadZone)))
        value.y = 0;

        return value;
    }

    public getConnectedGamepadsCount() : number{
        return this._gamepads.length;
    }

    public getConnectedGamepads(): Gamepad[]{
        return _.cloneDeep(this._gamepads);
    }

    private addEventListners(): void{
         window.addEventListener(
             "gamepadconnected",
             function(this: GamepadHandler, event: GamepadEvent) {
                 this.updateConnectedGamepads(event, true);
             }.bind(this) as EventListener
         );

         window.addEventListener(
             "gamepaddisconnected", 
             function(this: GamepadHandler, event: GamepadEvent){
                 this.updateConnectedGamepads(event, false); 
             }.bind(this) as EventListener
         );
    }

    private updateConnectedGamepads(event: GamepadEvent, connecting: boolean): void{
        let gamepad = event.gamepad;

        if(connecting){
            this._gamepads.push(gamepad);
            this._onGamepadConnected.dispatch(event);
        }
        else{
            this._gamepads.splice(gamepad.index, 1);
            this._onGamepadDisconnected.dispatch(event);
        }
    }

    private OnGamepadButtonDown(): void{
        for(let i = 0; i < this._gamepads.length; i++){
            if(this._buttonsStatus.has(this._gamepads[i].index)){
                let buttons = this._buttonsStatus.get(this._gamepads[i].index)!;

                for(let j = 0; j < buttons.length; j++){
                    if(this.getButtonDown(j, i)){
                        let dataObject = new GamepadData(i, j, JoyStick.NONE, buttons[j]);
                        this._onGamepadButtonDown.dispatch(dataObject);
                    }
                }
            }
        }
    }

    private OnGamepadButtonUp(): void{
        for(let i = 0; i < this._gamepads.length; i++){
            if(this._buttonsStatus.has(this._gamepads[i].index)){
                let buttons = this._buttonsStatus.get(this._gamepads[i].index)!;

                for(let j = 0; j < buttons.length; j++){
                    if(this.getButtonUp(j, i)){
                        let dataObject = new GamepadData(i, j, JoyStick.NONE, buttons[j]);
                        this._onGamepadButtonUp.dispatch(dataObject);
                    }
                }
            }
        }
    }

    private OnGamepadButton(): void{
        for(let i = 0; i < this._gamepads.length; i++){
            if(this._buttonsStatus.has(this._gamepads[i].index)){
                let buttons = this._buttonsStatus.get(this._gamepads[i].index)!;

                for(let j = 0; j < buttons.length; j++){
                    if(this.getButton(j, i)){
                        let dataObject = new GamepadData(i, j, JoyStick.NONE, buttons[j]);
                        this._onGamepadButtonPressed.dispatch(dataObject);
                    }
                }
            }
        }
    }

    private OnGamepadTriggerAxisChanged(): void{
        for(let i = 0; i < this._gamepads.length; i++){

            let buttons = this._buttonsStatus.get(this._gamepads[i].index);
            let cacheButtons = this._buttonsCache.get(this._gamepads[i].index);

            if(buttons != undefined && cacheButtons != undefined){
                for(let j = 0; j < buttons.length; j++){
                    
                    let cacheResult = cacheButtons[j].value;
                    let result = this.getButtonValue(j, i);

                    if(cacheResult != result){
                        let changedTrigger = new GamepadData(i, j, JoyStick.NONE, buttons[j]);
                        this._onGamepadTriggerAxisChanged.dispatch(changedTrigger);
                    }
                }
            }
        }
    }

    private OnGamepadJoystickAxis(joystick: JoyStick): void{
        for(let i = 0; i < this._gamepads.length; i++){
            let result = this.getJoystickAxis(joystick, i);
            let joystickAxises = new GamepadData(i, ControllerButton.NONE, joystick, undefined, result);
            this._onGamepadJoystickAxis.dispatch(joystickAxises);       
        }
    }

    private isGamepadIndexValid(gamepadIndex: number): boolean{
        let gamepads = navigator.getGamepads ? navigator.getGamepads() : [];

        if(gamepads[gamepadIndex] == null)
            return false;
        else
            return true;
    }

    private dispatchEvents(): void{
        this.OnGamepadButtonDown();
        this.OnGamepadButton();
        this.OnGamepadButtonUp();
        this.OnGamepadTriggerAxisChanged();
        this.OnGamepadJoystickAxis(JoyStick.LEFTSTICK);
        this.OnGamepadJoystickAxis(JoyStick.RIGHTSTICK);
    }

    private clearAndSetCache(){        
        //clear cache
        this._buttonsCache.clear(); 
        // move the buttons status from the previous frame to the cache
        this._buttonsStatus.forEach((value: GamepadButton[], key: number) => this._buttonsCache.set(key, value)); 
        //clear button status
        this._buttonsStatus.clear();
    }
}

export default GamepadHandler;
